            {
                childFirstName: '', 
                gender: '', 
                age: 0,
                shoe: '', 
                childNotes: ''
            }
        ];


    const setFirstName = (e) => {
        child.childFirstName = e.target.value;
        props.sampleSet(e.target.value)
    }

    const setGender = (e) => {
        child.gender = e.target.value;
        props.setGender(e.target.value);
    }

        //onChange={() => props.setChildrenState(child.childFirstName)}

